mapper.update:
  		update wms_attr_set

			attr_set_name=#{attrSetName},
			remark=#{remark}


			id=#{id}
			and not exists (select 1 from wms_attr_set where id!=#{id} and attr_set_name = #{attrSetName})

mapper.delete:
		delete from wms_attr_set 
	 	 where id = #{id}
   		   and not exists (select 1 from wms_sku_attr where attr_set_id = #{id})
