mapper.update:
UPDATE WMS_INVOICE SET FP_DM = #{fpDm, jdbcType=VARCHAR}, FP_HM = #{fpHm, jdbcType=VARCHAR}, KPRQ = #{kprq, jdbcType=TIME}, RETURN_CODE = #{returnCode, jdbcType=VARCHAR}, OP_DATE = #{opDate, jdbcType=TIME}, OP_USER = #{opUser, jdbcType=VARCHAR}, STATUS = #{status, jdbcType=VARCHAR}, JSON_DATA = #{jsonData, jdbcType=VARCHAR}, INVOICE_TYPE = #{invoiceType, jdbcType=VARCHAR}, MOBILE = #{mobile, jdbcType=VARCHAR}, EMAIL = #{email, jdbcType=VARCHAR}, PREVIEW_IMG_URL = #{previewImgUrl, jdbcType=VARCHAR}, PDF_URL = #{pdfUrl, jdbcType=VARCHAR}, EWM_URL = #{ewmUrl, jdbcType=VARCHAR} WHERE
ID = #{id}
ORDER_CODE = #{orderCode}

UPDATE WMS_INVOICE
FP_DM = #{fpDm, jdbcType=VARCHAR}, FP_HM = #{fpHm, jdbcType=VARCHAR}, KPRQ = #{kprq, jdbcType=TIME}, RETURN_CODE = #{returnCode, jdbcType=VARCHAR}, OP_DATE = #{opDate, jdbcType=TIME}, OP_USER = #{opUser, jdbcType=VARCHAR}, STATUS = #{status, jdbcType=VARCHAR}, JSON_DATA = #{jsonData, jdbcType=VARCHAR}, INVOICE_TYPE = #{invoiceType, jdbcType=VARCHAR}, MOBILE = #{mobile, jdbcType=VARCHAR}, EMAIL = #{email, jdbcType=VARCHAR}, PREVIEW_IMG_URL = #{previewImgUrl, jdbcType=VARCHAR}, PDF_URL = #{pdfUrl, jdbcType=VARCHAR}, EWM_URL = #{ewmUrl, jdbcType=VARCHAR}, KPLSH = #{kpLsh, jdbcType=VARCHAR}, CHLSH = #{chLsh, jdbcType=VARCHAR}
WHERE
ID = #{id}
ORDER_CODE = #{orderCode}

mapper.select:
SELECT
    i.ORDER_CODE,
    i.FP_DM,
    i.FP_HM,
    o.ORDER_SOURCE,
    TO_CHAR(i.KPRQ, 'yyyy-mm-dd hh24:mi:ss') KPRQ,
    i.STATUS,
    i.KPLSH,
    i.CHLSH,
    i.INVOICE_TYPE
FROM
  WMS_INVOICE I
LEFT JOIN WMS_SALES_ORDER O ON i.ORDER_CODE = o.ORDER_CODE

    AND i.ORDER_CODE = #{orderCode}
    AND i.FP_DM = #{fpDm}
    AND i.FP_HM = #{fpHm}
    AND i.RETURN_CODE = #{returnCode}
    AND i.STATUS = #{status}
    AND i.INVOICE_TYPE = #{invoiceType}
    AND i.OP_DATE >= #{opDateBegin}
    AND i.OP_DATE <= #{opDateEnd}
    AND i.MOBILE = #{mobile}
    AND i.KPLSH = #{kpLsh}
    AND i.CHLSH = #{chLsh}
    AND ROWNUM <= 4000

SELECT ORDER_CODE
FROM WMS_INVOICE
WHERE STATUS IN #{status}
