mapper.insert:
insert into WMS_MANUAL_REISSUE_ORDER_GOODS(ID, MANUAL_REISSUE_ORDER_ID, SKU_CODE, SKU_NAME, QTY) select WMS_SEQ_MANUAL_REISSUE_ORDER.nextval C0, A.* from (
select #{item.manualReissueOrderId} C1, #{item.skuCode} C2, #{item.skuName} C3, #{item.qty} C4 from dual
) A

mapper.update:
update WMS_MANUAL_REISSUE_ORDER
new_order_code = #{newOrderCode, jdbcType=VARCHAR}, platform = #{platform, jdbcType=NUMERIC}, invoice = #{invoice, jdbcType=NUMERIC}, bill_Type = #{billType, jdbcType=VARCHAR}, remark = #{remark, jdbcType=VARCHAR}, extension = #{extension, jdbcType=VARCHAR}, status = #{status, jdbcType=NUMERIC}, UPDATE_BY = #{updateBy, jdbcType=VARCHAR}, ORDER_JSON = #{orderJson, jdbcType=VARCHAR}, UPDATE_TIME = sysdate
where id = #{id}

mapper.select:
SELECT * FROM ( SELECT A.*, ROWNUM r FROM ( SELECT T1.ID, T1.PLATFORM, T1.ORDER_CODE, T1.NEW_ORDER_CODE, T1.INVOICE, T1.BILL_TYPE, T1.REMARK, T1.EXTENSION, T1.STATUS, T1.CREATE_TIME, T1.CREATE_BY, T1.UPDATE_TIME, T1.UPDATE_BY, T2.ORDER_AMOUNT, T2.PAYMENT_NAME, T2.CONSIGNEE, T2.MOBILE FROM WMS_MANUAL_REISSUE_ORDER T1 LEFT JOIN WMS_SALES_ORDER T2 ON T1.ORDER_CODE = T2.ORDER_CODE
LEFT JOIN WMS_MANUAL_REISSUE_ORDER_GOODS T3 ON T1.ID = T3.MANUAL_REISSUE_ORDER_ID
order by id desc ) A WHERE rownum <= #{page.endRow} ) WHERE r >= #{page.startRow}
