mapper.select:
select t1.id as id, t1.shipping_code as shippingCode, t1.shipping_name as shippingName, t1.phone as phone, t1.contact as contact, t1.default_status as defaultStatus, t1.create_time as createTime, t1.remark as remark, t1.enabled as enabled, t1.template_name as templateName, t1.company_code as companyCode from wms_shipping t1
t1.id = #{id} and t1.shipping_code = #{shippingCode} and t1.shipping_name = #{shippingName} and t1.default_status = #{defaultStatus} and t1.enabled = #{enabled}
order by default_status desc

mapper.update:
update wms_shipping
shipping_name=#{shippingName}, phone=#{phone}, contact=#{contact}, default_status=#{defaultStatus}, remark=#{remark}, enabled=#{enabled}, template_name=#{templateName}, company_code=#{companyCode}
id = #{id} and shipping_code = #{shippingCode}
