mapper.select:
	select count(1) as total
	  from wms_stockin t1
 left join wms_warehouse t2
	    on t1.warehouse_code = t2.warehouse_code

   		t1.id=#{id}
   		and t1.stockin_code=#{stockInCode}
   		and t1.original_code=#{originalCode}
   		and t1.stockin_type=#{stockInType}
   		and t1.warehouse_code=#{warehouseCode}
   		and t1.handled_by=#{handledBy}
   		and (t1.handled_date between #{handledDateBegin} and #{handledDateEnd})

SELECT * FROM( SELECT A.*, rownum r FROM( select t1.id as stockin_id, t1.stockin_code as stockin_code, t1.stockin_type as stockin_type, t1.opposite as stockin_opposite, t1.original_code as stockin_original_code, t1.handled_by as stockin_handled_by, t1.handled_date as stockin_handled_date, t1.prepared_by as stockin_prepared_by, t1.prepared_time as stockin_prepared_time, t1.remark as stockin_remark, t1.handling_status as stockin_handling_status, t1.enabled as stockin_enabled, t1.finished_time as stockin_finished_time, t2.id as detail_id, t2.batch_number as detail_batch_number, t2.quantity as detail_quantity, t2.remark as detail_remark, t2.measure_unit as detail_measure_unit, t2.indiv_enabled as detail_indiv_enabled, t2.indiv_finished as detail_indiv_finished, t2.wares_status as detail_wares_status, t2.original_code as detail_original_code, t3.id as sku_id, t3.sku_code as sku_code, t3.sku_name as sku_name, t3.sku_barcode as sku_barcode, t4.id as warehouse_id, t4.warehouse_code as warehouse_code, t4.warehouse_name as warehouse_name from wms_stockin t1 join wms_stockin_goods t2 on t2.stockin_code = t1.stockin_code join wms_sku t3 on t2.sku_id = t3.id left join wms_warehouse t4 on t1.warehouse_code= t4.warehouse_code
t1.id=#{stockInId} and t1.stockin_code=#{stockInCode} and t2.id=#{stockInItemId} and t1.handling_status=#{handlingStatus} and t4.warehouse_code=#{warehouseCode} and t3.sku_code=#{skuCode} and t3.sku_name like '%${skuName}%' and t2.wares_status=#{waresStatus} and t1.finished_time>=#{finishedTimeBegin} and trunc(t1.finished_time)<=#{finishedTimeEnd}
) A
WHERE rownum<=#{page.endRow} ) WHERE r >= #{page.startRow}

	select count(1) as total
	  from wms_stockin t1
	  join wms_stockin_goods t2
	    on t2.stockin_code = t1.stockin_code
	  join wms_sku t3
	    on t2.sku_id = t3.id
 left join wms_warehouse t4
		on t1.warehouse_code = t4.warehouse_code

   		t1.id=#{stockInId}
   		and t1.stockin_code=#{stockInCode}
   		and t2.id=#{stockInItemId}
   		and t1.handling_status=#{handlingStatus}
   		and t4.warehouse_code=#{warehouseCode}
   		and t3.sku_code=#{skuCode}
   		and t3.sku_name like '%${skuName}%'
   		and t2.wares_status=#{waresStatus}
   		and t1.finished_time>=#{finishedTimeBegin}
		and trunc(t1.finished_time)<=#{finishedTimeEnd}

		SELECT *
		 FROM( 
			  SELECT A.*, rownum r
		  	  FROM(
		  	  		select t1.warehouse_code        as warehouseCode,
		  	  			   t3.id                  as skuId,
		  	  		       t2.wares_status        as waresStatus,
		  	  		       max(t4.warehouse_name) as warehouseName,
		  	  		       max(t3.sku_code)       as skuCode,
		  	  		       max(t3.sku_name)       as skuName,
		  	  		       max(t2.measure_unit)   as measureUnit,  
		  	  		       sum(t2.quantity)       as quantity
					  from wms_stockin t1
					  join wms_stockin_goods t2
					    on t2.stockin_code = t1.stockin_code
					  join wms_sku t3
					    on t2.sku_id = t3.id
					  join wms_warehouse t4
    					on t1.warehouse_code = t4.warehouse_code

				   		t1.handling_status=#{handlingStatus}
				   		and t1.stockin_code=#{stockInCode}
				   		and t4.warehouse_code=#{warehouseCode}
				   		and t3.sku_code=#{skuCode}
				   		and t3.sku_name like '%${skuName}%'
				   		and t2.wares_status=#{waresStatus}
				   		and t1.finished_time>=#{finishedTimeBegin}
	   					and trunc(t1.finished_time)<=#{finishedTimeEnd}

				    group by t1.warehouse_code, t3.id, t2.wares_status
		  	  	   ) A

			  WHERE rownum<=#{page.endRow} 
			)
	    WHERE r >= #{page.startRow}

	select count(1) as total
	 from (select 1
	  from wms_stockin t1
	  join wms_stockin_goods t2
	    on t2.stockin_code = t1.stockin_code
	  join wms_sku t3
	    on t2.sku_id = t3.id
	  join wms_warehouse t4
		on t1.warehouse_code = t4.warehouse_code

   		t1.handling_status=#{handlingStatus}
   		and t1.stockin_code=#{stockInCode}
   		and t4.warehouse_code=#{warehouseCode}
   		and t3.sku_code=#{skuCode}
   		and t3.sku_name like '%${skuName}%'
   		and t2.wares_status=#{waresStatus}
   		and t1.finished_time>=#{finishedTimeBegin}
		and trunc(t1.finished_time)<=#{finishedTimeEnd}

    group by t1.warehouse_code, t3.sku_code
    )

mapper.update:
update wms_stockin
opposite=#{opposite}, warehouse_code=#{warehouse.warehouseCode}, original_code=#{originalCode}, handled_by=#{handledBy}, handled_date=#{handledDate}, finished_Time=#{finishedTime}, handling_status=#{handlingStatus}, enabled=#{enabled}, remark=#{remark},
where id = #{id}

mapper.delete:
	delete from wms_stockin 
 	 where id = #{id}
 	   and handling_status=0

mapper.insert:
insert into wms_stockin_goods(id, stockin_code, sku_id,quantity,batch_number,remark) select WMS_SEQ_COMMON.nextval,A.* from(
select #{item.stockIn.stockInCode},#{item.sku.id},#{item.quantity},#{item.batchNumber},#{item.remark} from dual
)A

mapper.update:
update wms_stockin_goods
quantity=#{quantity}, batch_number=#{batchNumber}, wares_status=#{waresStatus}, remark=#{remark},
where id = #{id}
