mapper.select:
select t1.id as id, t1.warehouse_code as warehouseCode, t1.warehouse_name as warehouseName, t1.warehouse_address as warehouseAddress, t1.warehouse_phone as warehousePhone, t1.warehouse_contact as warehouseContact, t1.warehouse_type as warehouseType, t1.default_status as defaultStatus, t1.create_time as createTime, t1.remark as remark, t1.enabled as enabled from wms_warehouse t1
t1.id = #{id} and t1.warehouse_code = #{warehouseCode} and t1.warehouse_name = #{warehouseName} and t1.default_status = #{defaultStatus} and t1.enabled = #{enabled}
order by default_status desc

mapper.update:
update wms_warehouse
warehouse_address=#{warehouseAddress}, warehouse_phone=#{warehousePhone}, warehouse_contact=#{warehouseContact}, warehouse_type=#{warehouseType}, default_status=#{defaultStatus}, remark=#{remark}, enabled=#{enabled},
id = #{id} and warehouse_code = #{warehouseCode}
