mapper.select:
SELECT * FROM( SELECT A.*, rownum r FROM( select t1.id as wares_id, t1.wares_name as wares_name, t1.wares_code as wares_code, t1.wares_brand as wares_brand, t1.measure_unit as wares_measure_unit, t1.indiv_enabled as wares_indiv_enabled, t1.create_time as wares_create_time, t1.enabled as wares_enabled, t1.wares_model as wares_model, t1.wares_remark as wares_remark, t2.id as cat_id, t2.cat_name as cat_name, t3.id as attr_set_id, t3.attr_set_name as attr_set_name from wms_wares t1 join wms_attr_set t3 on t1.attr_set_id = t3.id left join wms_wares_category t2 on t1.cat_id = t2.id
t1.id=#{id} and t1.wares_name like '%${waresName}%' and t1.wares_code=#{waresCode} and instr(t2.cat_path,#{catPath}) = 1
) A
WHERE rownum<=#{page.endRow} ) WHERE r >= #{page.startRow}

mapper.update:
update wms_wares
cat_id=#{category.id}, attr_set_id=#{attrSet.id}, wares_name=#{waresName}, wares_code=#{waresCode}, wares_brand=#{waresBrand}, measure_unit=#{measureUnit}, indiv_enabled=#{indivEnabled}, enabled=#{enabled}, wares_model=#{waresModel}, wares_remark=#{waresRemark}
where id=#{id}
and not exists (select 1 from wms_wares where id!=#{id} and wares_name=#{waresName})
and not exists (select 1 from wms_wares where id!=#{id} and wares_code=#{waresCode})

mapper.delete:
		delete from wms_wares 
	 	 where id = #{id}
   		   and not exists (select 1 from wms_sku where wares_id = #{id})

mapper.select:
		SELECT *
		 FROM( 
			  SELECT A.*, rownum r
		  	  FROM(
		  	  		select 
					  from wms_sku t1
					  join wms_wares t2
					    on t1.wares_id = t2.id
				 left join wms_wares_category t4
              			on t2.cat_id = t4.id

				   		t1.id=#{id}
				   		and t1.wares_id=#{waresId}
				   		and t1.sku_code=#{skuCode}
				   		and t1.sku_name like '%${skuName}%'
				   		and t1.sku_name=#{skuFullName}
				   		and t1.sku_barcode=#{skuBarcode}
				   		and instr(t4.cat_path,#{catPath}) = 1
				   		and t2.indiv_enabled=#{indivEnabled}

		  	  	   ) A

			  WHERE rownum<=#{page.endRow} 
			)
	    WHERE r >= #{page.startRow}

		select count(1) as total
		  from wms_sku t1
		  join wms_wares t2
		    on t1.wares_id = t2.id
	 left join wms_wares_category t4
            on t2.cat_id = t4.id

	   		t1.id=#{id}
	   		and t1.wares_id=#{waresId}
	   		and t1.sku_code=#{skuCode}
	   		and t1.sku_name like '%${skuName}%'
	   		and t1.sku_name=#{skuFullName}
	   		and t1.sku_barcode=#{skuBarcode}
	   		and instr(t4.cat_path,#{catPath}) = 1

SELECT * FROM( SELECT A.*, rownum r FROM( select t1.id as sku_id, t1.sku_code as sku_code, t1.sku_name as sku_name, t1.sku_barcode as sku_barcode, t1.item_ids as sku_item_ids, t1.create_time as sku_create_time, t1.remark as sku_remark, t1.enabled as sku_enabled, t2.id as wares_id, t2.wares_name as wares_name, t2.measure_unit as wares_measure_unit, t2.indiv_enabled as wares_indiv_enabled, t2.wares_model as wares_model, t2.wares_remark as wares_remark, t7.id as attr_id, t7.attr_name as attr_name, t6.id as item_id, t6.item_name as item_name from wms_sku t1 join wms_wares t2 on t1.wares_id = t2.id left join wms_wares_category t4 on t2.cat_id = t4.id left join wms_sku_item_relation t5 on t5.sku_id = t1.id left join wms_sku_item t6 on t5.sku_item_id = t6.id left join wms_sku_attr t7 on t6.attr_id = t7.id
t1.id=#{id} and t1.wares_id=#{waresId} and t1.sku_code=#{skuCode} and t1.sku_name like '%${skuName}%' and t1.sku_name=#{skuFullName} and t1.sku_barcode=#{skuBarcode} and instr(t4.cat_path,#{catPath}) = 1
) A
WHERE rownum<=#{page.endRow} ) WHERE r >= #{page.startRow}

	select 
	  from wms_sku t1
	  join wms_wares t2
	    on t1.wares_id = t2.id

   		t1.sku_code=#{skuCode}
   		or t1.sku_name=#{skuFullName}
   		or t1.sku_barcode=#{skuBarcode}
   		or t1.material_code=#{materialCode}

mapper.insert:
 		insert into wms_sku_item_relation(id, sku_id, sku_item_id)
		select WMS_SEQ_COMMON.nextval,A.* from(

		   select #{skuId},#{itemId} from dual

		  )A
		  where not exists (select 1 from wms_sku_item_relation where sku_id = #{skuId} and sku_item_id in 

	 	 	#{itemId}

	 	)

insert into wms_sku_bom_detail(id, P_SKU_CODE, C_SKU_CODE, QUANTITY, IS_BONUS) select WMS_SEQ_COMMON.nextval, A.* from (
select #{item.pSkuCode} P_SKU_CODE, #{item.cSkuCode} C_SKU_CODE, NULL quantity, #{item.quantity} quantity, 1 isBonus #{item.isBonus} isBonus from dual
) A WHERE NOT EXISTS ( SELECT 1 FROM WMS_SKU_BOM_DETAIL WHERE a.P_SKU_CODE = P_SKU_CODE AND A.C_SKU_CODE = C_SKU_CODE )

mapper.update:
update wms_sku
sku_name=#{skuName}, sku_code=#{skuCode}, sku_barcode=#{skuBarcode}, remark=#{remark}, enabled=#{enabled}, material_code=#{materialCode},
where id=#{id}

mapper.delete:
		delete from wms_sku 
	 	 where id = #{id}
   		   and not exists (select 1 from wms_stock where sku_id = #{id})
