mapper.select:
	SELECT count(1)
  from WMS_LOG


      TYPE = #{type,jdbcType=INTEGER}


     and  OP_NAME like '%'||#{opName,jdbcType=VARCHAR}||'%'


     and  CONTENT like '%'||#{content,jdbcType=VARCHAR}||'%'


     and IP = #{ip,jdbcType=VARCHAR}


     and OP_USER_ID = #{opUserId,jdbcType=VARCHAR}


     and OP_USER_NAME = #{opUserName,jdbcType=VARCHAR}


     and OP_TIME   >=  to_date(#{startDate,jdbcType=TIMESTAMP},'yyyy-MM-dd hh24:mi:ss')


     and OP_TIME   <=  to_date(#{endDate,jdbcType=TIMESTAMP},'yyyy-MM-dd hh24:mi:ss')

  	SELECT *
	  FROM( 
		SELECT t2.*, rownum r
		FROM(
    select 

    from WMS_LOG


        TYPE = #{type,jdbcType=INTEGER}


       and  OP_NAME = #{opName_hit,jdbcType=VARCHAR}


       and  OP_NAME like '%'||#{opName,jdbcType=VARCHAR}||'%'


       and  CONTENT like '%'||#{content,jdbcType=VARCHAR}||'%'


       and IP = #{ip,jdbcType=VARCHAR}


       and OP_USER_ID = #{opUserId,jdbcType=VARCHAR}


       and OP_USER_NAME = #{opUserName,jdbcType=VARCHAR}


       and OP_TIME   >=  to_date(#{startDate,jdbcType=TIMESTAMP},'yyyy-MM-dd hh24:mi:ss')


       and OP_TIME   <=  to_date(#{endDate,jdbcType=TIMESTAMP},'yyyy-MM-dd hh24:mi:ss')


          order by OP_TIME desc      
      )t2

			  WHERE rownum<=#{endRow} 
			)
	    WHERE r >= #{startRow}
